import React, {useEffect} from 'react'
import axios from 'axios'

const ShowProducts = () => {

    //const [products, setProducts] = useState([])

    useEffect(()=> {
        const getProductsData = async () => {
            const {data} = await axios.get('/api/products/allProducts')
            console.log(data)
        }
        getProductsData()
    }, [])
    return (
        <>
        <h1>All Products</h1>
        </>
    )
}

export default ShowProducts
